package com.example.demo.controller;import com.example.demo.payload.Mail;import com.example.demo.services.IMailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping(path = "/api/v1/email")public class EmailController {    @Autowired    private IMailService mailService;    @RequestMapping(path = "", method = RequestMethod.POST)    public ResponseEntity<?> sendEmail(@RequestBody Mail mail) {        return new ResponseEntity<>(mailService.sendEmail(mail), HttpStatus.OK);    }}