package com.example.demo.enums;public enum ResponseStatus {    SUCCESS("500", "Success"), FAIL("501", "Fail");    private final String code;    private final String desc;    ResponseStatus(String code, String desc) {        this.code = code;        this.desc = desc;    }    public String getCode() {        return code;    }    public String getDesc() {        return desc;    }    public static ResponseStatus fromCode(String code) {        if (code != null) {            for (ResponseStatus status : ResponseStatus.values()) {                if (status.getCode().equals(code)) {                    return status;                }            }        }        return null;    }    public static ResponseStatus fromDesc(String desc) {        if (desc != null) {            for (ResponseStatus status : ResponseStatus.values()) {                if (status.getDesc().equals(desc)) {                    return status;                }            }        }        return null;    }}