package com.example.demo.services.impls;import com.example.demo.enums.ResponseStatus;import com.example.demo.payload.Mail;import com.example.demo.response.Response;import com.example.demo.services.IMailService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.mail.javamail.JavaMailSender;import org.springframework.mail.javamail.MimeMessageHelper;import org.springframework.stereotype.Service;import javax.mail.internet.MimeMessage;@Service@Slf4jpublic class MailService implements IMailService {    @Autowired    private JavaMailSender mailSender;    @Override    public Response<Boolean> sendEmail(Mail mail) {        Response<Boolean> response = new Response<>(ResponseStatus.FAIL);        try {            MimeMessage message = mailSender.createMimeMessage();            MimeMessageHelper helper = new MimeMessageHelper(message);            helper.setTo(mail.getMailTo());            helper.setSubject(mail.getMailSubject());            helper.setText(mail.getMailContent());            mailSender.send(message);            log.info("Send email success");        } catch (Exception e) {            e.printStackTrace();        }        return response;    }}